async function seedQuotes() {
  try {
    await connectDB();
    console.log('Connected to MongoDB');

    // Find Varanasi city ID (optional - for linking quotes to city)
    const varanasiCity = await City.findOne({ 'name.en': 'Varanasi' });
    const cityId = varanasiCity?._id;

    // Clear existing quotes (optional - comment out if you want to keep existing)
    // await Quote.deleteMany({});
    // console.log('Cleared existing quotes');

    // Insert quotes
    for (const quoteData of quotesData) {
      const existingQuote = await Quote.findOne({
        author: quoteData.author,
        'quote.en': quoteData.quote.en,
      });

      if (!existingQuote) {
        const quote = new Quote({
          ...quoteData,
          cityId: cityId || undefined,
        });
        await quote.save();
        console.log(`тЬУ Added quote by ${quoteData.author}`);
      } else {
        // IMPORTANT:
        // If you re-run the seed script after changing fields (e.g. adding Cloudinary image URLs),
        // we should update the existing record instead of skipping it.
        //
        // Only overwrite `image` when a non-empty URL is provided in `quotesData`
        // (prevents accidental wiping if a placeholder is left blank).
        const updatePayload: Record<string, unknown> = {
          quote: quoteData.quote,
          author: quoteData.author,
          source: quoteData.source || undefined,
          order: quoteData.order,
          isActive: quoteData.isActive,
          cityId: cityId || existingQuote.cityId || undefined,
        };

        if (quoteData.image && quoteData.image.trim() !== '') {
          updatePayload.image = quoteData.image;
        }

        existingQuote.set(updatePayload);

        if (existingQuote.isModified()) {
          await existingQuote.save();
          console.log(`тЖ║ Updated quote by ${quoteData.author}`);
        } else {
          console.log(`тКШ Quote by ${quoteData.author} already up to date, skipping...`);
        }
      }
    }

    console.log('\nтЬЕ Quotes seeding completed!');
    console.log(`ЁЯУЭ Total quotes: ${await Quote.countDocuments()}`);
    process.exit(0);
  } catch (error) {
    console.error('Error seeding quotes:', error);
    process.exit(1);
  }
}

seedQuotes();
